import { React, Component } from 'react'
import { Row, Col, List, Card, BackTop, Button, Carousel, Image, Affix } from 'antd';
import FilmItem from './filmItem'
import Request from '../utils/axiosUtils'

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

import { faArrowUp } from '@fortawesome/free-solid-svg-icons'
import { withRouter } from 'react-router-dom'

import CommonUtils from '../utils/commonUtils'

import styled from 'styled-components'
import { EditOutlined } from '@ant-design/icons';

const Updiv = styled.div`
     height: 40;
  width: 40;
  line-height: 40px;
  border-radius: 4;
  background: #1088e9;
  color: #fff;
  text-align: center;
  font-size: 14;
`;
const CarouselDiv = styled(Col)`
margin-top:10px;
`
const EditButtonDiv=styled(Button)`
border-radius: 10px;
font-size: 20px;
font-weight: 600;
&.ant-btn-lg{
    padding-top:0px;
    padding-bottom:0px;

}

`
class HomeNew extends Component {
    constructor(props) {
        super(props);
        this.state = {
            picList: []
        };

    }

    componentWillMount() {
        Request.get("/api/carousel/getCarouselList").then(res => {
            if (res.data.resCode == 0) {
                this.setState({
                    picList: res.data.resMessage
                });
            }
        });
    }

    clickToEdit() {
        let { history } = this.props;
         history.push({pathname: '/editPage'})
        //history.push({ pathname: '/postDetailPage' })

    }
    generateImageList(item) {
        const uuid = CommonUtils.guid()
        const url = "http://localhost:3000/api/img/getPic/carousel/" + item.picId
        return (
            <Row key={uuid}>
                <Col span={16} offset={4}>
                    <Image
                        height={600}
                        fallback="data:image/png;base64,"
                        src={url}></Image>
                </Col>
            </Row>);
    }
    render() {

        console.log("a");
        const listItem = this.state.picList.map((item) => this.generateImageList(item));


        return (
            <>
             <Affix offsetBottom={30} style={{position: "absolute",right: 100,bottom: 50}}>
                            <EditButtonDiv type="primary"  onClick={() => this.clickToEdit()} size="large" icon={<EditOutlined/>}>
                                Edit Recipe
                             </EditButtonDiv>

                        </Affix>
                <Row >
                    <CarouselDiv span={24}>
                        <Carousel autoplay>
                            {listItem}
                        </Carousel>
                    </CarouselDiv>
                    <Col span={16} offset={4}>
                        <Row align="middle">
                            <Col span={2} offset={10}>
                                <h1>Recipe</h1>
                            </Col>
                            
                        </Row>
                    </Col>

                </Row>
                {/* <Row>
                    <Col span={2} offset={18}>
                        <Affix offsetBottom={30}>
                            <Button type="primary"  onClick={() => this.clickToEdit()}>
                                Affix bottom
                             </Button>

                        </Affix>
                    </Col>
                </Row>
             */}
            </>
        );
    }
}
export default withRouter(HomeNew);